import{_ as e,c as a,o as r,d as h}from"./app.adce8baa.js";const _=JSON.parse('{"title":"\u6D4F\u89C8\u5668","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u6D4F\u89C8\u5668\u7F13\u5B58","slug":"\u6D4F\u89C8\u5668\u7F13\u5B58"},{"level":3,"title":"service worker","slug":"service-worker"},{"level":3,"title":"Memory Cache","slug":"memory-cache"},{"level":3,"title":"Disk Cache","slug":"disk-cache"},{"level":3,"title":"Push Cache","slug":"push-cache"},{"level":3,"title":"\u7F13\u5B58\u7B56\u7565","slug":"\u7F13\u5B58\u7B56\u7565"},{"level":2,"title":"\u6E32\u67D3\u539F\u7406","slug":"\u6E32\u67D3\u539F\u7406"},{"level":2,"title":"\u963B\u585E\u6E32\u67D3","slug":"\u963B\u585E\u6E32\u67D3"},{"level":2,"title":"\u672C\u5730\u5B58\u50A8","slug":"\u672C\u5730\u5B58\u50A8"},{"level":2,"title":"web \u5B89\u5168","slug":"web-\u5B89\u5168"},{"level":3,"title":"\u8DE8\u57DF\uFF08\u540C\u6E90\u7B56\u7565\uFF09","slug":"\u8DE8\u57DF\uFF08\u540C\u6E90\u7B56\u7565\uFF09"},{"level":2,"title":"\u4E8B\u4EF6\u5FAA\u73AF\u673A\u5236","slug":"\u4E8B\u4EF6\u5FAA\u73AF\u673A\u5236"}],"relativePath":"frontend/browser.md","lastUpdated":1717459260000}'),i={name:"frontend/browser.md"},c=h('<h1 id="\u6D4F\u89C8\u5668" tabindex="-1">\u6D4F\u89C8\u5668 <a class="header-anchor" href="#\u6D4F\u89C8\u5668" aria-hidden="true">#</a></h1><h2 id="\u6D4F\u89C8\u5668\u7F13\u5B58" tabindex="-1">\u6D4F\u89C8\u5668\u7F13\u5B58 <a class="header-anchor" href="#\u6D4F\u89C8\u5668\u7F13\u5B58" aria-hidden="true">#</a></h2><h3 id="service-worker" tabindex="-1">service worker <a class="header-anchor" href="#service-worker" aria-hidden="true">#</a></h3><p>Service Worker \u662F\u8FD0\u884C\u5728\u6D4F\u89C8\u5668\u80CC\u540E\u7684\u72EC\u7ACB\u7EBF\u7A0B\uFF0C\u4E00\u822C\u53EF\u4EE5\u7528\u6765\u5B9E\u73B0\u7F13\u5B58\u529F\u80FD \u4F7F\u7528 Service Worker\u7684\u8BDD\uFF0C\u4F20\u8F93\u534F\u8BAE\u5FC5\u987B\u4E3A HTTPS,\u56E0\u4E3A Service Worker \u4E2D\u6D89\u53CA\u5230\u8BF7\u6C42\u62E6\u622A\uFF0C\u6240\u4EE5\u5FC5\u987B\u4F7F\u7528 HTTPS \u534F\u8BAE\u6765\u4FDD\u969C\u5B89\u5168 \u6B65\u9AA4</p><p>\u6CE8\u518C Service Worker \u76D1\u542C\u5230 install \u4E8B\u4EF6\u4EE5\u540E\u5C31\u53EF\u4EE5\u7F13\u5B58\u9700\u8981\u7684\u6587\u4EF6 \u4E0B\u6B21\u7528\u6237\u8BBF\u95EE\u7684\u65F6\u5019\u5C31\u53EF\u4EE5\u901A\u8FC7\u62E6\u622A\u8BF7\u6C42\u7684\u65B9\u5F0F\u67E5\u8BE2\u662F\u5426\u5B58\u5728\u7F13\u5B58</p><p>\u662F:\u53EF\u4EE5\u76F4\u63A5\u8BFB\u53D6\u7F13\u5B58\u6587\u4EF6 \u5426:\u8BF7\u6C42\u6570\u636E</p><p>Service Worker \u7684\u7F13\u5B58\u4E0E\u6D4F\u89C8\u5668\u5176\u4ED6\u5185\u5EFA\u7684\u7F13\u5B58\u673A\u5236\u4E0D\u540C\uFF0C\u5B83\u53EF\u4EE5\u8BA9\u6211\u4EEC\u81EA\u7531\u63A7\u5236\u7F13\u5B58\u54EA\u4E9B\u6587\u4EF6\u3001\u5982\u4F55\u5339\u914D\u7F13\u5B58\u3001\u5982\u4F55\u8BFB\u53D6\u7F13\u5B58\uFF0C\u5E76\u4E14\u7F13\u5B58\u662F\u6301\u7EED\u6027\u7684 \u5F53 Service Worker \u6CA1\u6709\u547D\u4E2D\u7F13\u5B58\u7684\u65F6\u5019\uFF0C\u5C31\u9700\u8981\u53BB\u8C03\u7528 fetch \u51FD\u6570\u83B7\u53D6\u6570\u636E \u4E5F\u5C31\u662F\u8BF4\uFF0C\u5F53\u6CA1\u6709\u5728 Service Worker \u547D\u4E2D\u7F13\u5B58\u7684\u8BDD\uFF0C\u4F1A\u6839\u636E\u7F13\u5B58\u67E5\u627E\u4F18\u5148\u7EA7\u53BB\u67E5\u627E\u6570\u636E \u4F46\u662F\u4E0D\u7BA1\u662F\u4ECE Memory Cache \u4E2D\u8FD8\u662F\u4ECE\u7F51\u7EDC\u8BF7\u6C42\u4E2D\u83B7\u53D6\u7684\u6570\u636E\uFF0C\u6D4F\u89C8\u5668\u90FD\u4F1A\u5148\u4ECE Service Worker \u4E2D\u83B7\u53D6\u7684\u5185\u5BB9</p><h3 id="memory-cache" tabindex="-1">Memory Cache <a class="header-anchor" href="#memory-cache" aria-hidden="true">#</a></h3><p>Memory Cache \u4E5F\u5C31\u662F\u5185\u5B58\u4E2D\u7684\u7F13\u5B58\uFF0C\u8BFB\u53D6\u5185\u5B58\u4E2D\u7684\u6570\u636E\u6BD4\u78C1\u76D8\u5FEB</p><p>\u5185\u5B58\u7F13\u5B58\u867D\u7136\u8BFB\u53D6\u9AD8\u6548\uFF0C\u4F46\u662F\u7F13\u5B58\u6301\u7EED\u6027\u5F88\u77ED\uFF0C\u4F1A\u968F\u7740\u8FDB\u7A0B\u7684\u91CA\u653E\u800C\u91CA\u653E\u3002\u4E00\u65E6\u5173\u95ED Tab \u9875\u9762\uFF0C\u5185\u5B58\u4E2D\u7684\u7F13\u5B58\u5C31\u88AB\u91CA\u653E\u4E86</p><p>\u5F53\u6211\u4EEC\u8BBF\u95EE\u8FC7\u9875\u9762\u4EE5\u540E\uFF0C\u518D\u6B21\u5237\u65B0\u9875\u9762\uFF0C\u53EF\u4EE5\u53D1\u73B0\u5F88\u591A\u6570\u636E\u90FD\u6765\u81EA\u4E8E\u5185\u5B58\u7F13\u5B58</p><p>\u6D4F\u89C8\u5668\u4F1A\u628A\u54EA\u4E9B\u6587\u4EF6\u653E\u5165\u5185\u5B58\u2753</p><p>\u6D4F\u89C8\u5668\u4F1A\u628A\u89E3\u6790\u5B8C\u6210\u7684js\u4E0Ecss\u653E\u5165\u5185\u5B58\u4E2D</p><p>\u7279\u70B9</p><p>\u5FEB\u901F\u8BFB\u53D6\uFF1A\u5C06\u7F16\u8BD1\u89E3\u6790\u540E\u7684\u6587\u4EF6\uFF0C\u76F4\u63A5\u5B58\u5165\u8BE5\u8FDB\u7A0B\u7684\u5185\u5B58\u4E2D\uFF0C\u5360\u636E\u8BE5\u8FDB\u7A0B\u4E00\u5B9A\u7684\u5185\u5B58\u8D44\u6E90\uFF0C\u4EE5\u65B9\u4FBF\u4E0B\u6B21\u8FD0\u884C\u4F7F\u7528\u65F6\u7684\u5FEB\u901F\u8BFB\u53D6 \u65F6\u6548\u6027\uFF1A\u4E00\u65E6\u8BE5\u8FDB\u7A0B\u5173\u95ED\uFF0C\u5219\u8BE5\u8FDB\u7A0B\u7684\u5185\u5B58\u5219\u4F1A\u6E05\u7A7A</p><h3 id="disk-cache" tabindex="-1">Disk Cache <a class="header-anchor" href="#disk-cache" aria-hidden="true">#</a></h3><p>Disk Cache \u4E5F\u5C31\u662F\u5B58\u50A8\u5728\u786C\u76D8\u4E2D\u7684\u7F13\u5B58\uFF0C\u8BFB\u53D6\u901F\u5EA6\u6162\u70B9\uFF0C\u4F46\u662F\u4EC0\u4E48\u90FD\u80FD\u5B58\u50A8\u5230\u78C1\u76D8\u4E2D\uFF0C\u6BD4\u4E4B Memory Cache \u80DC\u5728\u5BB9\u91CF\u548C\u5B58\u50A8\u65F6\u6548\u6027\u4E0A \u5728\u6240\u6709\u6D4F\u89C8\u5668\u7F13\u5B58\u4E2D\uFF0CDisk Cache \u8986\u76D6\u9762\u57FA\u672C\u662F\u6700\u5927\u7684 \u53EF\u4EE5\u6839\u636E HTTP Herder \u4E2D\u7684\u5B57\u6BB5\u5224\u65AD\u54EA\u4E9B\u8D44\u6E90\u9700\u8981\u7F13\u5B58\uFF0C\u54EA\u4E9B\u8D44\u6E90\u53EF\u4EE5\u4E0D\u8BF7\u6C42\u76F4\u63A5\u4F7F\u7528\uFF0C\u54EA\u4E9B\u8D44\u6E90\u5DF2\u7ECF\u8FC7\u671F\u9700\u8981\u91CD\u65B0\u8BF7\u6C42 \u5373\u4F7F\u5728\u8DE8\u7AD9\u70B9\u7684\u60C5\u51B5\u4E0B\uFF0C\u76F8\u540C\u5730\u5740\u7684\u8D44\u6E90\u4E00\u65E6\u88AB\u786C\u76D8\u7F13\u5B58\u4E0B\u6765\uFF0C\u5C31\u4E0D\u4F1A\u518D\u6B21\u53BB\u8BF7\u6C42\u6570\u636E \u54EA\u4E9B\u8D44\u6E90\u4F1A\u88AB\u653E\u5165\u78C1\u76D8\u2753</p><p>\u5BF9\u4E8E\u5927\u6587\u4EF6\u6765\u8BF4\uFF0C\u5927\u6982\u7387\u662F\u4E0D\u5B58\u50A8\u5728\u5185\u5B58\u4E2D\u7684\uFF0C\u53CD\u4E4B\u4F18\u5148 \u5F53\u524D\u7CFB\u7EDF\u5185\u5B58\u4F7F\u7528\u7387\u9AD8\u7684\u8BDD\uFF0C\u6587\u4EF6\u4E5F\u4F1A\u4F18\u5148\u5B58\u50A8\u8FDB\u786C\u76D8</p><p>\u7279\u70B9</p><p>\u76F4\u63A5\u5C06\u7F13\u5B58\u5199\u5165\u786C\u76D8\u6587\u4EF6\u4E2D \u8BFB\u53D6\u9700\u8981\u5BF9\u786C\u76D8\u4E0A\u6587\u4EF6\u8FDB\u884CI/O\u64CD\u4F5C\uFF0C\u7136\u540E\u91CD\u65B0\u89E3\u6790\u8BE5\u7F13\u5B58\u5185\u5BB9\uFF0C\u8BFB\u53D6\u590D\u6742\uFF0C\u901F\u5EA6\u6BD4\u5185\u5B58\u7F13\u5B58\u6162 \u6587\u4EF6\u7C7B\u578B\u8986\u76D6\u9762\u5927 \u5BB9\u91CF\u5927\uFF0C\u5B58\u50A8\u65F6\u95F4\u53EF\u63A7</p><h3 id="push-cache" tabindex="-1">Push Cache <a class="header-anchor" href="#push-cache" aria-hidden="true">#</a></h3><p>Push Cache \u662F HTTP2 \u534F\u8BAE\u65B0\u589E\u7684\u5185\u5BB9 \u5F53\u4EE5\u4E0A\u4E09\u79CD\u7F13\u5B58\u90FD\u6CA1\u6709\u547D\u4E2D\u65F6\uFF0C\u5B83\u624D\u4F1A\u88AB\u4F7F\u7528\u3002\u5E76\u4E14\u7F13\u5B58\u65F6\u95F4\u4E5F\u5F88\u77ED\u6682\uFF0C\u53EA\u5728\u4F1A\u8BDD\uFF08Session\uFF09\u4E2D\u5B58\u5728\uFF0C\u4E00\u65E6\u4F1A\u8BDD\u7ED3\u675F\u5C31\u88AB\u91CA\u653E\u3002 \u7279\u70B9</p><p>\u6240\u6709\u7684\u8D44\u6E90\u90FD\u80FD\u88AB\u63A8\u9001\uFF0C\u4F46\u6709\u4E00\u5B9A\u7684\u517C\u5BB9\u6027\u95EE\u9898 \u53EF\u4EE5\u63A8\u9001 no-cache \u548C no-store \u7684\u8D44\u6E90 \u4E00\u65E6\u8FDE\u63A5\u88AB\u5173\u95ED\uFF0CPush Cache \u5C31\u88AB\u91CA\u653E \u591A\u4E2A\u9875\u9762\u53EF\u4EE5\u4F7F\u7528\u76F8\u540C\u7684 HTTP/2 \u8FDE\u63A5\uFF0C\u5373\u53EF\u4EE5\u4F7F\u7528\u540C\u4E00\u4EFD\u7F13\u5B58 Push Cache \u4E2D\u7684\u7F13\u5B58\u53EA\u80FD\u88AB\u4F7F\u7528\u4E00\u6B21 \u6D4F\u89C8\u5668\u53EF\u4EE5\u62D2\u7EDD\u63A5\u53D7\u5DF2\u7ECF\u5B58\u5728\u7684\u8D44\u6E90\u63A8\u9001</p><p>\u5F53\u6240\u6709\u7F13\u5B58\u90FD\u6CA1\u6709\u547D\u4E2D\u65F6,\u5C31\u4F1A\u53D1\u8D77\u7F51\u7EDC\u8BF7\u6C42\u6765\u83B7\u53D6\u8D44\u6E90</p><h3 id="\u7F13\u5B58\u7B56\u7565" tabindex="-1">\u7F13\u5B58\u7B56\u7565 <a class="header-anchor" href="#\u7F13\u5B58\u7B56\u7565" aria-hidden="true">#</a></h3><p>\u5F3A\u7F13\u5B58 \u534F\u5546\u7F13\u5B58 \u4F18\u5148\u7EA7\u8F83\u9AD8\u7684\u662F\u5F3A\u7F13\u5B58\uFF0C\u5F53\u5F3A\u7F13\u5B58\u5931\u6548\u7684\u60C5\u51B5\u4E0B\uFF0C\u624D\u4F1A\u8D70\u534F\u5546\u7F13\u5B58\u6D41\u7A0B</p><p>\u90FD\u662F\u901A\u8FC7\u8BBE\u7F6E HTTP Header \u6765\u5B9E\u73B0\u7684</p><h2 id="\u6E32\u67D3\u539F\u7406" tabindex="-1">\u6E32\u67D3\u539F\u7406 <a class="header-anchor" href="#\u6E32\u67D3\u539F\u7406" aria-hidden="true">#</a></h2><h2 id="\u963B\u585E\u6E32\u67D3" tabindex="-1">\u963B\u585E\u6E32\u67D3 <a class="header-anchor" href="#\u963B\u585E\u6E32\u67D3" aria-hidden="true">#</a></h2><h2 id="\u672C\u5730\u5B58\u50A8" tabindex="-1">\u672C\u5730\u5B58\u50A8 <a class="header-anchor" href="#\u672C\u5730\u5B58\u50A8" aria-hidden="true">#</a></h2><h2 id="web-\u5B89\u5168" tabindex="-1">web \u5B89\u5168 <a class="header-anchor" href="#web-\u5B89\u5168" aria-hidden="true">#</a></h2><p>XSS\uFF08\u8DE8\u7AD9\u811A\u672C\u653B\u51FB\uFF09 CSRF\uFF08\u8DE8\u7AD9\u8BF7\u6C42\u4F2A\u9020\uFF09</p><h3 id="\u8DE8\u57DF\uFF08\u540C\u6E90\u7B56\u7565\uFF09" tabindex="-1">\u8DE8\u57DF\uFF08\u540C\u6E90\u7B56\u7565\uFF09 <a class="header-anchor" href="#\u8DE8\u57DF\uFF08\u540C\u6E90\u7B56\u7565\uFF09" aria-hidden="true">#</a></h3><h2 id="\u4E8B\u4EF6\u5FAA\u73AF\u673A\u5236" tabindex="-1">\u4E8B\u4EF6\u5FAA\u73AF\u673A\u5236 <a class="header-anchor" href="#\u4E8B\u4EF6\u5FAA\u73AF\u673A\u5236" aria-hidden="true">#</a></h2>',34),s=[c];function d(t,l,n,o,p,u){return r(),a("div",null,s)}var f=e(i,[["render",d]]);export{_ as __pageData,f as default};
